import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { get, ref, update } from 'firebase/database';
import { fireDB } from '../firebase';
import { toast } from 'react-toastify';

const EditContact = () => {
  const { id } = useParams();
  const history = useHistory();

  const [contact, setContact] = useState({ name: '', email: '', contact: '' });

  useEffect(() => {
    const contactRef = ref(fireDB, `contacts/${id}`);
    get(contactRef)
      .then((snapshot) => {
        if (snapshot.exists()) {
          setContact(snapshot.val());
        } else {
          toast.error('Contact not found');
          history.push('/');
        }
      })
      .catch((error) => {
        toast.error(error.message);
        history.push('/');
      });
  }, [id, history]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContact((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { name, email, contact:conta } = contact;
    if (!name || !email || !contact) {
      toast.error('Please provide a value in each input field');
    } else {
      const contactRef = ref(fireDB, `contacts/${id}`);
      update(contactRef, contact)
        .then(() => {
          toast.success('Contact updated successfully');
          history.push('/');
        })
        .catch((error) => {
          toast.error(error.message);
        });
    }
  };

  return (
    <div style={{ marginTop: '100px' }}>
      <h2>Edit Contact</h2>
      <form style={{ margin: 'auto', padding: '15px', maxWidth: '400px', alignContent: 'center' }} onSubmit={handleSubmit}>
        <label htmlFor="name">Name</label>
        <input type="text" id="name" name="name" placeholder="Your name" value={contact.name} onChange={handleInputChange} />

        <label htmlFor="email">Email</label>
        <input type="email" id="email" name="email" placeholder="Your email" value={contact.email} onChange={handleInputChange} />

        <label htmlFor="contact">Contact</label>
        <input type="number" id="contact" name="contact" placeholder="Your contact number" value={contact.contact} onChange={handleInputChange} />

        <input type="submit" value="Update" />
      </form>
    </div>
  );
};

export default EditContact;
